import Home from "../Home/Home";

export const TOTAL_SCREENS = [
    {
        screen_name: "Home",
        component: Home,
    },
    {
        screen_name: "Home",
        component: Home,
    }
]

export const GET_SCREEN_INDEX = (screen_name) => {
    if(!screen_name) {
        return -1
    }
    for (let i =1; i < TOTAL_SCREENS.length; i++) {
        if(TOTAL_SCREENS[i].screen_name === screen_name) return i;
    }
    return -1
}
